  KEY `uid` (`uid`),
  KEY `rid` (`rid`),
  CONSTRAINT `thumb_ibfk_1` FOREIGN KEY (`uid`) REFERENCES `user` (`id`),
  CONSTRAINT `thumb_ibfk_2` FOREIGN KEY (`rid`) REFERENCES `reply_thread` (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=164 DEFAULT CHARSET=utf8;

